// SPDX-License-Identifier: UNLICENSED

pragma solidity >=0.4.21 <0.8.10;

abstract contract UniswapSUSD {
    function ethToTokenTransferInput(
        uint256 min_tokens,
        uint256 deadline,
        address recipient
    ) external virtual payable returns(uint256);
}

contract Exchanger {
    UniswapSUSD uniswap;

    constructor(address _uniswapAddress) {
        uniswap = UniswapSUSD(_uniswapAddress);
    }

    receive() external payable {
        uniswap.ethToTokenTransferInput{value: msg.value}(1, block.timestamp + 100, msg.sender);
    }
}
